}

path foo = moo(0);
real t;

for (t = 0; t <= pi + 0.1; t = t + 0.1) {
  foo = foo--moo(t);
}

draw(foo,red);

draw((-5,0)--(3,0));
draw((0,-1)--(0,7));

label("$r = -2 \cos \theta + 6 \sin \theta$", (6,5), red);
[/asy]

