}

path foo = moo(-44);
real t;

for (t = -44; t <= 44; t = t + 0.1) {
  foo = foo--moo(t);
}

draw(foo,red);
draw(reflect((0,0),(0,1))*(foo),red);

draw((-4,0)--(4,0));
draw((0,-4)--(0,4));

limits((-4,-4),(4,4),Crop);

label("$r^2 \cos 2 \theta = 4$", (6.5,1.5), red);
[/asy]
