A=(0,0);
B=(0,12);
C=(-16,0);
D=(-35,0);
draw(A--B--D--A);
draw(B--C);
draw((0,1.5)--(-1.5,1.5)--(-1.5,0));
label("$37$", (B+D)/2, NW);
label("$19$", (C+D)/2, S);
label("$16$", (A+C)/2, S);
label("A", A, SE);
label("B", B, NE);
label("D", D, SW);
label("C", C, S);
