draw((-4,-5)--(-4,5));
draw((-3,-5)--(-3,5));
draw((-2,-5)--(-2,5));
draw((-1,-5)--(-1,5));
draw((1,-5)--(1,5));
draw((2,-5)--(2,5));
draw((3,-5)--(3,5));
draw((4,-5)--(4,5));
draw((-5,-4)--(5,-4));
draw((-5,-3)--(5,-3));
draw((-5,-2)--(5,-2));
draw((-5,-1)--(5,-1));
draw((-5,1)--(5,1));
draw((-5,2)--(5,2));
